import React, { Component } from "react";
import {
  Grid,
  Row,
  Col,
  Table,

} from "react-bootstrap";
import Form from 'react-bootstrap/Form'

import { Card } from "components/Card/Card.jsx";

import "assets/css/customcss.css";
import Button from 'react-bootstrap/Button'
import { Api_route } from '../components/baseApi'
import axios from 'axios';
import Spinner from 'react-spinner-material';
//import Badge from 'react-bootstrap/Badge'
import 'materialize-css';
import Modal from 'react-bootstrap/Modal'

class UserProfile extends Component {
  constructor() {
    super()
    this.state = {
      change_panel: false,
      dataSource: [],
      isload_data: true,
      email: null,
      name: null,
      password: null,
      phone: null,
      role: "AGENT",
      conf_password: null,
      modalShow:fals,
      idUser:null,
    }




  }
  change_panel(etat) {
    this.setState({
      change_panel: etat,
      email: null,
      name: null,
      password: null,
      phone: null,
      role: "AGENT",
      conf_password: null,
    });
  }


  Get_user_from_api() {


    const url = "users?page=0&size=4"
    fetch(Api_route(url))
      .then((response) => response.json())
      .then((responseJson) => {
        this.setState({
          dataSource: responseJson.content,
          isload_data: false,
        })
        //console.log(responseJson);

      })
      .catch((error) => {

        console.log(error)

      })




  }

  componentDidMount() {
    this.Get_user_from_api();
  }

  changeHandler = e => {
    this.setState({ [e.target.name]: e.target.value });
  }
  submitHandeler = e => {
    e.preventDefault();
    const { email, name, password, phone, role, conf_password } = this.state;
    let Body = {
      "email": email,
      "name": name,
      "password": password,
      "phone": phone,
      "role": role

    };
    axios.post(Api_route("users?userId=1"), Body)
      .then(response => {
        this.Get_user_from_api();
        console.log(response);
      })
      .catch(error => {
        console.log(error);
      })

  }

  changeEtatuserHandeler(idUser){
    if(idUser){
      axios.patch(Api_route("users/disable-user?updatedBy=1&userId="+idUser))
      .then(response => {
        this.Get_user_from_api();
        console.log(response);
      })
      .catch(error => {
        console.log(error);
      })
    }
  }
  modalEvent(event,id){
    this.setState({
      modalEvent:event,
      idUser:id,
    });
  }
  render() {
    const { email, name, password, phone, role, conf_password } = this.state;
    // console.log(this.state.dataSource);

    return (

      <div className="content">



        {
          this.state.change_panel
            ?


            <Grid fluid>
              <Row>
                <Col md={12}>

